function Repair-CmnClientCert {
    [Cmdletbinding()]
    PARAM(
        [Parameter(Mandatory = $true, HelpMessage = 'List of computers to repair')]
        [String[]]$computers
    )
    foreach ($computer in $computers) {
        if (Test-Connection -ComputerName $computer -Count 1 -ErrorAction SilentlyContinue) {
            Write-Verbose -Message "Fixing $computer"
            Invoke-Command -ComputerName $computer -ScriptBlock { 
            Get-ChildItem -Path Cert:\LocalMachine\SMS | Where-Object { $_.Subject -match '^CN=SMS, ' } | Remove-Item ; 
            $ccmexecrunning = (get-service -name CcmExec).Status 
            If ($ccmexecrunning -eq "Running") {Write-Verbose -Message "Restarting ccmexec" | Restart-Service -Name CcmExec | Out-Null} }
        }
        else { Write-Verbose -Message "Unable to contact $computer" }
    }
    Get-PSSession
}
